import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import { IconButton } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Button from "@material-ui/core/Button";
import PostCard from "../SupportingProjectPostCard";
import SupportingProjectCommentCard from "../SupportingProjectCommentCard";

const useCStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    overflow: "hidden",
    padding: theme.spacing(0, 3),
  },
  paper: {
    maxWidth: 400,

    margin: `${theme.spacing(1)}px auto`,
    padding: theme.spacing(2),
  },
}));
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `vertical-tab-${index}`,
    "aria-controls": `vertical-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
    height: 800,
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
  },
}));

export default function SupportingProjectTab(props) {
  const classesC = useCStyles();

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const [comment, setComment] = useState("");

  const [allComments, setAllComments] = useState([]);
  const [allPosts, setAllPosts] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:3001/api/v1/projects/${props.project.id}`)
      .then((res) => res.json())
      .then((project) => {
        setAllComments(project.comments);
        setAllPosts(project.posts);
      });
  }, []);
  const addComment = () => {
    setOpen(false);
    fetch(`http://localhost:3001/api/v1/comments`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        project_id: props.project.id,
        user_id: props.user.id,
        blurb: comment,
      }),
    })
      .then((r) => r.json())
      .then((comment) => setAllComments([...allComments, comment]));
  };

  //   console.log(thisCommenter)
  return (
    <div>
      <h2>Community</h2>

      <div className={classes.root}>
        <Tabs
          orientation="vertical"
          variant="scrollable"
          value={value}
          onChange={handleChange}
          aria-label="Vertical tabs example"
          className={classes.tabs}
        >
          <Tab label="Updates" {...a11yProps(0)}></Tab>
          <Tab label="Comments" {...a11yProps(1)} />
        </Tabs>

        <TabPanel value={value} index={0}>
          {allPosts.map((p) => (
            <PostCard
              post={p}
              key={p.id}
              project={props.project}
              projectOwner={props.projectOwner}
            />
          ))}
        </TabPanel>
        <TabPanel value={value} index={1}>
          <IconButton aria-label="add" onClick={handleClickOpen}>
            <AddIcon />
          </IconButton>
          <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="form-dialog-title"
          >
            <DialogTitle id="form-dialog-title">Add a comment</DialogTitle>
            <DialogContent>
              <DialogContentText>
                Let the {props.project.title} community know your thoughts
              </DialogContentText>
              <TextField
                onChange={(e) => setComment(e.target.value)}
                autoFocus
                margin="dense"
                id="name"
                type="text"
                fullWidth
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose} color="primary">
                Cancel
              </Button>
              <Button onClick={addComment} color="primary">
                Add
              </Button>
            </DialogActions>
          </Dialog>

          <div className={classesC.root}>
            {allComments.map((c) => (
              <SupportingProjectCommentCard
                comment={c}
                key={c.id}
                project={props.project}
                projectOwner={props.projectOwner}
              />
            ))}
          </div>
        </TabPanel>
      </div>
    </div>
  );
}
