import React, { useEffect, useState } from 'react';
import { Input } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom';
import {useLocation} from "react-router-dom";
import { withRouter } from 'react-router-dom'


const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2),
    },
    resetContainer: {
        padding: theme.spacing(3),
    },
}));

function getSteps() {
    return ['1', '2', '3', '4', '5', '6', '7', '8'];
  }






function CreateProject() {
    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();
    const [title, setTitle] = useState('');
    const [story, setStory] = useState('');
    const [timeline, setTimeline] = useState(null);
    const [image, setImage] = useState('')
    const [fundraising_goal, setFundraisingGoal] = useState(null);
    const [github, setGithub] = useState('');
    const [language, setLanguage] = useState('');
    const [stage, setStage] = useState('');
    const data = useLocation();

    const [hasError, setErrors] = useState(false);
    const [project, setProject] = useState({});


    function submitValue ()  {
        const details = {
            'Title': title,
            'Story': story,
            'Timeline': timeline,
            'Fundraising Goal': fundraising_goal,
            'Image': image,
            'Github': github,
            'Language': language,
            'Stage': stage,
            'User': data.state.user

        }
        console.log(details);

    }
    useEffect(() => {

        async function fetchData() {
        const res = await fetch('http://localhost:3001/api/v1/projects', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'

            },
            body: JSON.stringify({ 
                    title: title,
                    story: story,
                    timeline: parseInt(timeline),
                    fundraising_goal: parseInt(fundraising_goal),
                    image: image,
                    github: github,
                    language: language,
                    stage: stage, 
                    user_id: data.state.user.id
            }),
        })
        .json()
        .then(res => setProject(res))
        .catch(err => setErrors(err))


    }
    fetchData();
})


    const handleNext = (e) => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);

    };

    const handleBack = () => {
