class Main extends React.Component {

state = {
    projects: [],
    user: {}

}



componentDidMount() {


    fetch(`http://localhost:3001/api/v1/users/${parseInt(localStorage.token)}`)
    .then(r => r.json())
    .then(user => {
        console.log(user)
        this.setState({
           projects: user.projects,
           user: user
        })
    }
    )
}


