import React, { useState } from 'react';
import { Input } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2),
    },
    resetContainer: {
        padding: theme.spacing(3),
    },
}));



function getSteps() {
    return ['Name project', 'Write your story', 'Upload your github'];
}

function getStepContent(step) {
    switch (step) {
        case 0:
            return `For each ad campaign that you create, you can control how much
              you're willing to spend on clicks and conversions, which networks
              and geographical locations you want your ads to show on, and more.`;
        case 1:
            return 'An ad group contains one or more ads which target a shared set of keywords.';
        case 2:
            return `Try out different ad text to see what brings in the most customers,
              and learn how to enhance your ads using features like ad extensions.
              If you run into any problems with your ads, find out how to tell if
              they're running and how to resolve approval issues.`;
        default:
            return 'Unknown step';
    }
}


export default function CreateProject(props) {
    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();
    const [title, setTitle] = useState('');
    const [story, setStory] = useState('');
    const [github, setGithub] = useState('');


    const submitValue = () => {
        const details = {
            'Title': title,
            'Story': story,
            'Github': github
        }
        console.log(details);

    }

    const handleNext = (e) => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);

    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };





    return (
        <div className={classes.root}>
            <Stepper activeStep={activeStep} orientation="vertical">

                <Step >
                    <StepLabel>Name your Project</StepLabel>
                    <StepContent>
                        <Typography>
                            <Input onChange={e => setTitle(e.target.value)} >
                                {/* {getStepContent(index)} */}

                            </Input>
                        </Typography>

                        <div className={classes.actionsContainer}>
                            <div>
                                <Button
                                    disabled={activeStep === 0}
                                    onClick={handleBack}
                                    className={classes.button}
                                >
                                    Back
                  </Button>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={handleNext}
                                    className={classes.button}
                                >
                                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                </Button>

                            </div>
                        </div>



                    </StepContent>
                </Step>
                <Step >
                    <StepLabel>Write your story</StepLabel>
                    <StepContent>
                        <Typography>
                            <Input onChange={e => setStory(e.target.value)} >
                                {/* {getStepContent(index)} */}

                            </Input>
                        </Typography>

                        <div className={classes.actionsContainer}>
                            <div>
                                <Button
                                    disabled={activeStep === 0}
                                    onClick={handleBack}
                                    className={classes.button}
                                >
                                    Back
                  </Button>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={handleNext}
                                    className={classes.button}
                                >
                                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                </Button>

                            </div>
                        </div>



                    </StepContent>
                </Step>
                <Step >
                    <StepLabel>Link your Github</StepLabel>
                    <StepContent>
                        <Typography>
                            <Input onChange={e => setGithub(e.target.value)} >
                                {/* {getStepContent(index)} */}

                            </Input>
                        </Typography>

                        <div className={classes.actionsContainer}>
                            <div>
                                <Button
                                    disabled={activeStep === 0}
                                    onClick={handleBack}
                                    className={classes.button}
                                >
                                    Back
                  </Button>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={handleNext}
                                    className={classes.button}
                                >
                                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                </Button>

                            </div>
                        </div>



                    </StepContent>
                </Step>

            </Stepper>
            {activeStep === steps.length && (
                <Paper square elevation={0} className={classes.resetContainer}>
                    <Typography>All steps completed - you&apos;re finished</Typography>
                    <Link to={{
                        pathname: "/new",
                        state: {
                            title,
                            story,
                            github,
                            user

                        }

                    }} >
                        <Button onClick={submitValue} className={classes.button}>
                            Create
          </Button>
                    </Link>
                    <Button onClick={handleReset} className={classes.button}>
                        Reset
          </Button>

                </Paper>
            )}
        </div>
    );
}
