import React from 'react';
import ProjectShowNav from './Navbars/ProjectShowNav';

import { makeStyles } from '@material-ui/core/styles';
import ProjectTabs from './Tabs/ProjectTabs';
import {useLocation} from "react-router-dom";
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import GitHubIcon from '@material-ui/icons/GitHub';
import { Link } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { IconButton } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import DeleteOutlinedIcon from '@material-ui/icons/DeleteOutlined';
import { Redirect } from 'react-router-dom';
import DialogContentText from '@material-ui/core/DialogContentText';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';



const useStyle = makeStyles({
  root: {
      minWidth: 275,
      height: 400
  },
  bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
  },
  title: {
      fontSize: 24,
  },
  pos: {
      marginBottom: 12,
  },
});


const useAccordionStyles = makeStyles((theme) => ({
  root: {
    width: '400%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}));

const ProjectShow = () =>  {

    const styles = useStyle();
    const accordionClasses = useAccordionStyles();


    const [open, setOpen] = React.useState(false);
    const [story, setStory] = React.useState('');
    const [timeline, setTimeline] = React.useState(null);
    const [fundraising_goal, setFundraisingGoal] = React.useState(null);
    const [deleteAlert, setDeleteAlert] = React.useState(false);
    const [deleted, setDelete] = React.useState(false)

    const handleStoryChange = (event) => {

      setStory(event.target.value) ;
    };

    const handleTimelineChange = (event) => {

      setTimeline(event.target.value) ;
    };

    const handleFundraisingChange = (event) => {

      setFundraisingGoal(event.target.value)
    };
    const handleClickOpen = () => {
      setOpen(true);
    };

    const handleClose = () => {
      setOpen(false);
    };
    const handleDeleteAlert = () => {
      setDeleteAlert(true)
    };

    const handleDelete = () => {
      setDelete(true);
      fetch(`http://localhost:3001/api/v1/projects/${data.state.project.id}`, {
        method: 'DELETE'
        })

    }


  // console.log(useLocation())
  const data = useLocation()



    // console.log(data.state)

    return (
      deleted ? <Redirect to="/my-projects" /> : 
  <div>


      <ProjectShowNav user={data.state.user} project={data.state.project}/>

      <div>
      <Card className={styles.root} variant="outlined">
            <CardContent>
                <Typography className={styles.title} color="textSecondary" gutterBottom>
                    {data.state.project.title}
                    <IconButton  onClick={handleClickOpen}><DeleteOutlinedIcon onClick={handleDeleteAlert} color="secondary"/>


                      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{'Are you sure you want to delete this project?'}</DialogTitle>

        <DialogActions>
          <Button onClick={handleClose} color="primary">
            No
          </Button>
          <Button onClick ={handleDelete} color="primary" autoFocus>
            Yes
          </Button>
        </DialogActions>
      </Dialog>



                    </IconButton>
          </Typography>




                   <IconButton >   
                   <div className={accordionClasses.root}>         
      <Accordion>

        <AccordionSummary
          expandIcon={< EditIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography className={accordionClasses.heading}>{data.state.project.story}</Typography>
        </AccordionSummary>
        <AccordionDetails>
        <TextField onChange={e => handleStoryChange(e)}
            autoFocus
            fullWidth
            value={data.state.project.story}

            fullWidth
          />
        </AccordionDetails>
      </Accordion>
      </div>
      </IconButton>


                   <IconButton >
                   <div className={accordionClasses.root}>         
      <Accordion>

        <AccordionSummary
          expandIcon={< EditIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography className={accordionClasses.heading}> timeline: {data.state.project.timeline} days</Typography>

          </AccordionSummary>
          <AccordionDetails>
          <TextField
            autoFocus
            margin="dense"
            value={data.state.project.timeline}
            fullWidth
          />


        </AccordionDetails>
          </Accordion>
          </div>        
        </IconButton>

        <IconButton >
                   <div className={accordionClasses.root}>         
      <Accordion>

        <AccordionSummary
          expandIcon={< EditIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography className={accordionClasses.heading}>fundraising goal: ${data.state.project.fundraising_goal} </Typography>

          </AccordionSummary>
          <AccordionDetails>
          <TextField onChange={e => handleFundraisingChange(e)}
            autoFocus
            margin="dense"
            id="name"
            value={data.state.project.fundraising_goal}
            fullWidth
          />


        </AccordionDetails>
          </Accordion>
          </div>        
        </IconButton>





                <Link href={data.state.project.github} >
                    <GitHubIcon />
                </Link><IconButton onClick={handleClickOpen}><EditIcon/></IconButton>



            </CardContent>

        </Card>
        </div>


      <ProjectTabs project={data.state.project} user={data.state.user} posts={data.state.posts}
                  supporters={data.state.supporters} comments={data.state.comments}
                  commenters={data.state.commenters} />




              </div>      





        )
    }


export default ProjectShow;
