import React, {useState} from 'react';
import  TextField  from '@material-ui/core/TextField';
import Button  from '@material-ui/core/Button';
import { withRouter } from 'react-router';
import { InputLabel } from '@material-ui/core';
import { FormControl } from '@material-ui/core';

class Login extends React.Component {
    state = {
      username: "",
      password: ""
    }

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value
  })
  }

  handleSubmit = (e) => {
    e.preventDefault()

    fetch('http://localhost:3001/api/v1/login',{
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        user: this.state
      })
    }).then(response => response.json())
    // .then(data => console.log(data.auth_key))
    .then(data => {
        // if(token.hasOwnProperty('token')){
            // console.log(data.auth_key)
            console.log(data)
            localStorage.setItem('token', data.user_info.user_id)
            this.props.handleLogin()
            this.props.history.push('/')
        // }else{
        //     alert('login failed...')
        // }
    })
  }



  render () {
    console.log(this.props.show)
  return (
    <section>

        <form onSubmit={this.handleSubmit} className="login-form" style={{width:"100%", maxWidth:"330px", padding:"15px", margin:"auto", height:"100%"}}>
            <h1>
                {/* <span className="font-weight-bold">VanBnB</span> */}
            </h1>
            <h2 className="text-center">Welcome</h2>
            <TextField> <InputLabel>Username</InputLabel>

                <FormControl name="username" placeholder="Username" onChange={this.handleInput} value={this.state.username}/>
            </TextField>
            <TextField> <InputLabel>Password</InputLabel>
                <FormControl name="password" type="password" placeholder="Password" onChange={this.handleInput} value={this.state.password}/>
            </TextField>
            <Button  type="submit" className="btn-lg btn-dark btn-block">Log in</Button>
            <div className="text-center pt-3">
                Or continue with social media
            </div>

        </form>

    </section>
    )
  }
  }
  export default withRouter(Login);


